step.sql:
SELECT P.*
 FROM AU_PT_POI P,  TEMP_PT_POI R
WHERE P.AUDATA_ID = R.PID;

SELECT P.*
 FROM AU_PT_POI_NAME P,  TEMP_PT_POI R
WHERE P.AUDATA_ID = R.PID;

SELECT P.*
 FROM AU_PT_ETA_ACCESS P,  TEMP_PT_ETA_ACCESS R
WHERE P.AUDATA_ID = R.PID;

SELECT P.*
 FROM AU_PT_ETA_STOP P,  TEMP_PT_ETA_STOP R
WHERE P.AUDATA_ID = R.PID;

SELECT P.*
 FROM AU_PT_LINE P,TEMP_PT_LINE T
WHERE  P.AUDATA_ID=T.PID;

SELECT P.*
 FROM AU_PT_LINE_NAME P,TEMP_PT_LINE T
WHERE  P.AUDATA_ID=T.PID;

SELECT P.*
 FROM AU_PT_ETA_LINE P,  TEMP_PT_ETA_LINE R
WHERE P.AUDATA_ID = R.PID;

SELECT P.*
 FROM AU_PT_STRAND P,TEMP_PT_STRAND T
WHERE  P.AUDATA_ID=T.PID;

SELECT P.*
 FROM AU_PT_STRAND_NAME P,TEMP_PT_STRAND T
WHERE  P.AUDATA_ID=T.PID;

SELECT P.*
 FROM AU_PT_COMPANY P,TEMP_PT_COMPANY T
WHERE P.AUDATA_ID=T.PID;

SELECT P.*
 FROM AU_PT_SYSTEM P,TEMP_PT_SYSTEM T
  WHERE P.AUDATA_ID=T.PID;
