SELECT /*+ORDERED USE_HASH(P,T)*/ P.*
   FROM IX_POI_CHILDREN P, TEMP_IX_POI_GROUP T,TEMP_IX_POI PP
 WHERE P.GROUP_ID = T.PID AND P.CHILD_POI_PID = PP.PID;

SELECT P.*
  FROM IX_POI_PARENT P, TEMP_IX_POI_GROUP T
 WHERE (P.GROUP_ID = T.PID);

SELECT P.*
  FROM IX_SAMEPOI P, TEMP_IX_SAMEPOI T
 WHERE P.GROUP_ID = T.PID;

DELETE FROM IX_POI_CHARGINGPLOT P WHERE P.CHARGING_ID IN (SELECT P.CHARGING_ID
                              FROM IX_POI_CHARGINGSTATION P, TEMP_IX_POI T
                             WHERE P.POI_PID = T.PID );
